import React, {
  createContext,
  useContext,
  useState,
  useEffect,
  ReactNode,
} from "react";

interface User {
  id: string;
  name: string;
  email: string;
  password: string;
  accountType: "individual" | "organization";
  companyName?: string; 
  taxId?: string; 
  profileImage?: string;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  login: (email: string, password: string) => Promise<boolean>;
  register: (
    name: string,
    email: string,
    password: string,
    accountType: "individual" | "organization",
    companyName?: string,
    taxId?: string
  ) => Promise<boolean>;
  logout: () => void;
  loading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const login = async (email: string): Promise<boolean> => {
    try {
      setLoading(true);
      await new Promise((resolve) => setTimeout(resolve, 1000));

      const existingUsers = JSON.parse(
        localStorage.getItem("registeredUsers") || "[]"
      );
      const userExists = existingUsers.find(
        (user: any) => user.email === email
      );
      if (!userExists) return false;

      const mockUser: User = {
        id: userExists.id,
        name: userExists.name,
        email: userExists.email,
        password: userExists.password,
        accountType: userExists.accountType,
        companyName: userExists.companyName,
        taxId: userExists.taxId,
      };

      setUser(mockUser);
      localStorage.setItem("user", JSON.stringify(mockUser));
      return true;
    } catch (error) {
      console.error("Login failed:", error);
      return false;
    } finally {
      setLoading(false);
    }
  };

  const register = async (
    name: string,
    email: string,
    password: string,
    accountType: "individual" | "organization",
    companyName?: string,
    taxId?: string
  ): Promise<boolean> => {
    try {
      setLoading(true);
      await new Promise((resolve) => setTimeout(resolve, 1000));

      const existingUsers = JSON.parse(
        localStorage.getItem("registeredUsers") || "[]"
      );
      const userExists = existingUsers.find(
        (user: any) => user.email === email
      );
      if (userExists) return false;

      const newUser = {
        id: Date.now().toString(),
        name: name.trim(),
        email,
        password, // لاحقًا يفضل تشفيره
        accountType,
        companyName: accountType === "organization" ? companyName : undefined,
        taxId: accountType === "organization" ? taxId : undefined,
        createdAt: new Date().toISOString(),
      };

      const updatedUsers = [...existingUsers, newUser];
      localStorage.setItem("registeredUsers", JSON.stringify(updatedUsers));

      const mockUser: User = {
        id: newUser.id,
        name: newUser.name,
        email: newUser.email,
        password: newUser.password, 
        accountType: newUser.accountType,
        companyName: newUser.companyName,
        taxId: newUser.taxId,
      };

      setUser(mockUser);
      localStorage.setItem("user", JSON.stringify(mockUser));
      return true;
    } catch (error) {
      console.error("Registration failed:", error);
      return false;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  const value: AuthContextType = {
    user,
    isAuthenticated: !!user,
    login,
    register,
    logout,
    loading,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

import {
  createContext,
  useContext,
  useState,
  ReactNode,
  useEffect,
} from "react";
import { useAuth } from "./AuthContext";
import { useFavorites } from "./FavoritesContext";
import { useNavigate } from "react-router-dom";
import { toast } from "sonner";
import { useTranslation } from "react-i18next";

type Product = {
  id: number;
  name: string;
  price: number;
  image: string;
};

type CartItem = Product & { quantity: number };

type CartContextType = {
  cart: CartItem[];
  addToCart: (product: Product) => void;
  removeFromCart: (id: number) => void;
  clearCart: () => void;
  increaseQuantity: (id: number) => void;
  decreaseQuantity: (id: number) => void;
  addToFavorites: (product: Product) => void;
};

const CartContext = createContext<CartContextType | undefined>(undefined);

export const CartProvider = ({ children }: { children: ReactNode }) => {
  const { isAuthenticated, user } = useAuth();
  const { addToFavorites } = useFavorites();
  const navigate = useNavigate();
  const { t } = useTranslation();

  // 🔑 مفتاح التخزين: لكل يوزر أو guest
  const storageKey = isAuthenticated && user ? `cart_${user.id}` : "cart_guest";

  const [cart, setCart] = useState<CartItem[]>([]);

  // ✅ تحميل السلة عند أول مرة
  useEffect(() => {
    const saved = localStorage.getItem(storageKey);
    if (saved) {
      setCart(JSON.parse(saved));
    }
  }, [storageKey]);

  // ✅ حفظ السلة في localStorage مع كل تعديل
  useEffect(() => {
    localStorage.setItem(storageKey, JSON.stringify(cart));
  }, [cart, storageKey]);

  // ➕ إضافة منتج للسلة
  const addToCart = (product: Product) => {
    if (!isAuthenticated) {
      toast.warning(t("cart.loginRequired"), {
        action: { label: t("auth.login"), onClick: () => navigate("/login") },
      });
      return;
    }
    setCart((prev) => {
      const existing = prev.find((item) => item.id === product.id);
      if (existing) {
        toast.success(t("cart.quantityIncreased"));
        return prev.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      toast.success(t("cart.addedSuccess", { product: product.name }));
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const handleAddToFavorites = (product: Product) => {
    if (!isAuthenticated) {
      toast.warning(t("favorites.loginRequired"), {
        action: { label: t("auth.login"), onClick: () => navigate("/login") },
      });
      return;
    }
    addToFavorites(product);
    toast.success(t("favorites.addedSuccess", { product: product.name }));
  };

  const removeFromCart = (id: number) => {
    const removed = cart.find((item) => item.id === id);
    setCart((prev) => prev.filter((item) => item.id !== id));
    if (removed) {
      toast.info(t("cart.removedSuccess", { product: removed.name }));
    }
  };

  const clearCart = () => {
    setCart([]);
    toast.info(t("cart.clearedSuccess"));
  };

  const increaseQuantity = (id: number) => {
    setCart((prev) =>
      prev.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
    toast.success(t("cart.quantityIncreased"));
  };

  const decreaseQuantity = (id: number) => {
    setCart((prev) =>
      prev
        .map((item) =>
          item.id === id ? { ...item, quantity: item.quantity - 1 } : item
        )
        .filter((item) => item.quantity > 0)
    );
    toast.info(t("cart.quantityDecreased"));
  };

  return (
    <CartContext.Provider
      value={{
        cart,
        addToCart,
        removeFromCart,
        clearCart,
        increaseQuantity,
        decreaseQuantity,
        addToFavorites: handleAddToFavorites,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) throw new Error("useCart must be used within CartProvider");
  return context;
};

import {
  createContext,
  useContext,
  useState,
  ReactNode,
  useEffect,
} from "react";
import { useAuth } from "./AuthContext";
import { useNavigate } from "react-router-dom";
import { toast } from "sonner";
import { useTranslation } from "react-i18next";

type Product = {
  id: number;
  name: string;
  price: number;
  image: string;
};

type FavoritesContextType = {
  favorites: Product[];
  addToFavorites: (product: Product) => void;
  removeFromFavorites: (id: number) => void;
  clearFavorites: () => void;
  isFavorite: (id: number) => boolean;
};

const FavoritesContext = createContext<FavoritesContextType | undefined>(
  undefined
);

export const FavoritesProvider = ({ children }: { children: ReactNode }) => {
  const { isAuthenticated, user } = useAuth();
  const navigate = useNavigate();
  const { t } = useTranslation();

  // 🔑 مفتاح التخزين حسب المستخدم أو guest
  const storageKey =
    isAuthenticated && user ? `favorites_${user.id}` : "favorites_guest";

  const [favorites, setFavorites] = useState<Product[]>([]);

  // ✅ تحميل المفضلة عند أول مرة
  useEffect(() => {
    const saved = localStorage.getItem(storageKey);
    if (saved) {
      setFavorites(JSON.parse(saved));
    }
  }, [storageKey]);

  // ✅ حفظ المفضلة في localStorage عند أي تعديل
  useEffect(() => {
    localStorage.setItem(storageKey, JSON.stringify(favorites));
  }, [favorites, storageKey]);

  // ❤️ إضافة إلى المفضلة
  const addToFavorites = (product: Product) => {
    if (!isAuthenticated) {
      toast.warning(t("favorites.loginRequired"), {
        action: {
          label: t("auth.login"),
          onClick: () => navigate("/login"),
        },
      });
      return;
    }

    setFavorites((prev) => {
      const exists = prev.find((item) => item.id === product.id);
      if (exists) {
        toast.info(t("favorites.removeFromFavorites"));
        return prev.filter((item) => item.id !== product.id);
      }
      toast.success(t("favorites.addedSuccess", { product: product.name }));
      return [...prev, product];
    });
  };

  // ❌ إزالة من المفضلة
  const removeFromFavorites = (id: number) => {
    const removed = favorites.find((item) => item.id === id);
    setFavorites((prev) => prev.filter((item) => item.id !== id));
    if (removed) {
      toast.info(t("favorites.removedSuccess", { product: removed.name }));
    }
  };

  // 🚮 مسح كل المفضلة
  const clearFavorites = () => {
    setFavorites([]);
    toast.info(t("favorites.clearAll"));
  };

  // 🔍 هل المنتج موجود في المفضلة؟
  const isFavorite = (id: number) => favorites.some((item) => item.id === id);

  return (
    <FavoritesContext.Provider
      value={{
        favorites,
        addToFavorites,
        removeFromFavorites,
        clearFavorites,
        isFavorite,
      }}
    >
      {children}
    </FavoritesContext.Provider>
  );
};

export const useFavorites = () => {
  const context = useContext(FavoritesContext);
  if (!context)
    throw new Error("useFavorites must be used within FavoritesProvider");
  return context;
};

import React, { createContext, useContext, useEffect, useState } from 'react';

type Theme = 'light' | 'dark';

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

interface ThemeProviderProps {
  children: React.ReactNode;
}

export const ThemeProvider: React.FC<ThemeProviderProps> = ({ children }) => {
  const [theme, setTheme] = useState<Theme>(() => {
    const savedTheme = localStorage.getItem('theme') as Theme;
    return savedTheme || 'light';
  });

  useEffect(() => {
    const root = window.document.documentElement;
    root.classList.remove('light', 'dark');
    root.classList.add(theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

