import React, { useState } from "react";
import { useAuth } from "@/contexts/AuthContext";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useNavigate } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { toast } from "sonner";
import { Camera } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

const SettingsPage: React.FC = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [isEditing, setIsEditing] = useState(false);
  const [openPreview, setOpenPreview] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || "",
    email: user?.email || "",
    companyName: user?.companyName || "",
    taxId: user?.taxId || "",
    profileImage: user?.profileImage || "",
  });
  const [loading, setLoading] = useState(false);

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-muted-foreground">{t("common.loading")}</div>
      </div>
    );
  }

  const handleInputChange = (field: string, value: string) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      handleInputChange("profileImage", reader.result as string);
    };
    reader.readAsDataURL(file);
  };

  const handleSave = async () => {
    setLoading(true);
    try {
      const updatedUser = { ...user, ...formData };
      localStorage.setItem("user", JSON.stringify(updatedUser));
      toast.success(t("settings.updateSuccess"));
      setIsEditing(false);
      window.location.reload();
    } catch (error) {
      toast.error(t("settings.updateError"));
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    setFormData({
      name: user?.name || "",
      email: user?.email || "",
      companyName: user?.companyName || "",
      taxId: user?.taxId || "",
      profileImage: user?.profileImage || "",
    });
    setIsEditing(false);
  };

  const profileImg =
    formData.profileImage ||
    "https://ui-avatars.com/api/?name=" +
      encodeURIComponent(user?.name || "User") +
      "&background=random&color=fff";

  return (
    <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle>{t("settings.title")}</CardTitle>
            {!isEditing && (
              <Button onClick={() => setIsEditing(true)}>
                {t("settings.editProfile")}
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* العمود الأول - الصورة + الاسم */}
            <div className="flex flex-col items-center text-center gap-3">
              <div className="relative">
                <img
                  src={profileImg}
                  alt="Profile"
                  className="w-28 h-28 rounded-full object-cover border cursor-pointer"
                  onClick={() =>
                    isEditing
                      ? document.getElementById("profileImage")?.click()
                      : setOpenPreview(true)
                  }
                />
                {isEditing && (
                  <div className="absolute bottom-0 right-0 bg-black/70 rounded-full p-1 pointer-events-none">
                    <Camera className="w-5 h-5 text-white" />
                  </div>
                )}
              </div>

              {/* input مخفي */}
              <input
                id="profileImage"
                type="file"
                accept="image/*"
                className="hidden"
                onChange={handleImageUpload}
              />

              <div className="mt-2">
                <h2 className="text-lg font-semibold">
                  {formData.name || user.name}
                </h2>
              </div>
            </div>

            {/* العمود الثاني - باقي البيانات */}
            <div className="md:col-span-2 space-y-6">
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div>
                  <div className="text-sm text-muted-foreground mb-1">
                    {t("auth.name")}
                  </div>
                  {isEditing ? (
                    <Input
                      value={formData.name}
                      onChange={(e) =>
                        handleInputChange("name", e.target.value)
                      }
                      placeholder={t("auth.enterName")}
                    />
                  ) : (
                    <div className="font-medium">{user.name}</div>
                  )}
                </div>

                <div>
                  <div className="text-sm text-muted-foreground mb-1">
                    {t("auth.email")}
                  </div>
                  {isEditing ? (
                    <Input
                      type="email"
                      value={formData.email}
                      onChange={(e) =>
                        handleInputChange("email", e.target.value)
                      }
                      placeholder={t("auth.enterEmail")}
                    />
                  ) : (
                    <div className="font-medium">{user.email}</div>
                  )}
                </div>

                <div>
                  <div className="text-sm text-muted-foreground mb-1">
                    {t("auth.accountType")}
                  </div>
                  <div className="font-medium capitalize">
                    {user.accountType}
                  </div>
                </div>

                {user.accountType === "organization" && (
                  <>
                    <div>
                      <div className="text-sm text-muted-foreground mb-1">
                        {t("auth.companyName")}
                      </div>
                      {isEditing ? (
                        <Input
                          value={formData.companyName}
                          onChange={(e) =>
                            handleInputChange("companyName", e.target.value)
                          }
                          placeholder={t("auth.enterCompanyName")}
                        />
                      ) : (
                        <div className="font-medium">
                          {user.companyName || "-"}
                        </div>
                      )}
                    </div>

                    <div>
                      <div className="text-sm text-muted-foreground mb-1">
                        {t("auth.taxId")}
                      </div>
                      {isEditing ? (
                        <Input
                          value={formData.taxId}
                          onChange={(e) =>
                            handleInputChange("taxId", e.target.value)
                          }
                          placeholder={t("auth.enterTaxId")}
                        />
                      ) : (
                        <div className="font-medium">{user.taxId || "-"}</div>
                      )}
                    </div>
                  </>
                )}
              </div>

              {/* الأزرار */}
              <div className="flex gap-2 pt-2">
                {isEditing ? (
                  <>
                    <Button onClick={handleSave} disabled={loading}>
                      {loading ? t("common.loading") : t("settings.save")}
                    </Button>
                    <Button variant="outline" onClick={handleCancel}>
                      {t("common.cancel")}
                    </Button>
                  </>
                ) : (
                  <>
                    <Button variant="secondary" onClick={() => navigate("/")}>
                      {t("common.back")}
                    </Button>
                    <Button variant="destructive" onClick={logout}>
                      {t("auth.logout")}
                    </Button>
                  </>
                )}
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Dialog لمعاينة الصورة */}
      <Dialog open={openPreview} onOpenChange={setOpenPreview}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>{t("settings.profileImage")}</DialogTitle>
          </DialogHeader>
          <img
            src={profileImg}
            alt="Preview"
            className="w-full h-auto rounded-lg"
          />
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default SettingsPage;

import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Card, CardHeader, CardContent, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useNavigate } from "react-router-dom";
import { useCart } from "../contexts/CartContext";
import { useFavorites } from "../contexts/FavoritesContext";
import { ShoppingCart, Box, Heart } from "lucide-react";
import { fetchProducts, Product } from "../mockProductsApi";
import { useTranslation } from "react-i18next";

const StorePage: React.FC = () => {
  const navigate = useNavigate();
  const { cart, addToCart, increaseQuantity, decreaseQuantity } = useCart();
  const { favorites, addToFavorites, removeFromFavorites, isFavorite } =
    useFavorites();
  const { t } = useTranslation();
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProducts().then((data) => {
      setProducts(data);
      setLoading(false);
    });
  }, []);

  const handleAddToCart = (product: Product) => addToCart(product);

  const handleToggleFavorite = (product: Product) => {
    if (isFavorite(product.id)) {
      removeFromFavorites(product.id);
    } else {
      addToFavorites(product);
    }
  };
  const goToCheckout = () => {
    const totalPrice = cart.reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    navigate("/checkout", { state: { cart, totalPrice } });
  };

  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="min-h-screen py-12 bg-muted/50 pt-24 dark:bg-gray-900 dark:text-gray-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* شريط الأزرار */}
        <div className="flex justify-between items-center mb-6 -mt-2">
          <div className="flex gap-3">
            <Button
              variant="outline"
              className="text-blue-600 border-blue-600 hover:bg-blue-50 dark:text-blue-300 dark:border-blue-300 dark:hover:bg-gray-800 rounded-full px-4 py-2 flex items-center gap-2"
              onClick={() => navigate("/orders")}
            >
              <Box className="w-5 h-5" />
              {t("nav.order")}
            </Button>
            <Button
              variant="outline"
              className="text-red-600 border-red-600 hover:bg-red-50 dark:text-red-300 dark:border-red-300 dark:hover:bg-gray-800 rounded-full px-4 py-2 flex items-center gap-2"
              onClick={() => navigate("/favorites")}
            >
              <Heart className="w-5 h-5" />
              {t("store.favorites")} ({favorites.length})
            </Button>
          </div>
          <Button
            onClick={goToCheckout}
            className="relative flex items-center rounded-full bg-blue-600 hover:bg-blue-700 text-white dark:bg-blue-700 dark:hover:bg-blue-800"
          >
            <ShoppingCart className="w-6 h-6 " />
            {totalItems > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-600 text-white text-xs w-5 h-5 flex items-center justify-center rounded-full">
                {totalItems}
              </span>
            )}
          </Button>
        </div>
        {/* شبكة المنتجات */}
        {loading ? (
          <div className="text-center py-12">{t("common.loading")}</div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {products.map((product) => (
              <motion.div
                key={product.id}
                whileHover={{ scale: 1.05 }}
                className="rounded-lg shadow-md overflow-hidden bg-white dark:bg-gray-800"
              >
                <Card className="h-full flex flex-col bg-gradient-to-br from-white via-blue-50 to-blue-100 dark:from-gray-900 dark:via-gray-800 dark:to-gray-800 shadow-xl border-0 rounded-2xl transition-all duration-300 hover:scale-[1.03] hover:shadow-2xl relative">
                  <CardHeader className="pb-0">
                    <div className="flex justify-between items-start">
                      <CardTitle className="text-xl font-bold text-blue-900 dark:text-blue-300 flex-1">
                        {product.name}
                      </CardTitle>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleToggleFavorite(product)}
                        className={`p-2 rounded-full transition-all duration-200 ${
                          isFavorite(product.id)
                            ? "text-red-500 hover:text-red-600 hover:bg-red-50"
                            : "text-gray-400 hover:text-red-500 hover:bg-red-50"
                        }`}
                      >
                        <Heart
                          className={`h-5 w-5 ${
                            isFavorite(product.id) ? "fill-current" : ""
                          }`}
                        />
                      </Button>
                    </div>
                  </CardHeader>
                  <CardContent className="flex flex-col items-center pt-2">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="w-full h-48 object-cover mb-4 rounded-xl border border-blue-200 dark:border-gray-700 shadow-sm"
                    />
                    <p className="text-lg font-semibold mb-2 text-blue-700 dark:text-blue-200">
                      {product.price} {t("common.currency")}
                    </p>
                    <Button
                      onClick={() => handleAddToCart(product)}
                      className="mt-auto mb-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-2 rounded-full shadow dark:bg-blue-700 dark:hover:bg-blue-800"
                    >
                      {t("cart.add")}
                    </Button>
                    {(() => {
                      const cartItem = cart.find(
                        (item) => item.id === product.id
                      );
                      if (cartItem) {
                        return (
                          <div className="flex items-center gap-2 mt-2">
                            <Button
                              variant="outline"
                              size="sm"
                              className="border-blue-600 text-blue-600 hover:bg-blue-50 dark:border-blue-300 dark:text-blue-300 dark:hover:bg-gray-800"
                              onClick={() => decreaseQuantity(product.id)}
                            >
                              -
                            </Button>
                            <span className="font-bold text-blue-900 dark:text-blue-200">
                              {cartItem.quantity}
                            </span>
                            <Button
                              variant="outline"
                              size="sm"
                              className="border-blue-600 text-blue-600 hover:bg-blue-50 dark:border-blue-300 dark:text-blue-300 dark:hover:bg-gray-800"
                              onClick={() => increaseQuantity(product.id)}
                            >
                              +
                            </Button>
                          </div>
                        );
                      }
                      return null;
                    })()}
                  </CardContent>
                </Card>
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default StorePage;

const API_BASE_URL =
  import.meta.env.VITE_API_URL || "http://192.168.1.6:8000/api";

// HTTP helpers below cover common verbs

class ApiClient {
  private baseURL: string;

  constructor(baseURL: string) {
    this.baseURL = baseURL;
  }

  private async request<T>(
    endpoint: string,
    options: RequestInit = {}
  ): Promise<T> {
    const url = `${this.baseURL}${endpoint}`;
    const token = localStorage.getItem("authToken");

    const config: RequestInit = {
      headers: {
        "Content-Type": "application/json",
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    };

    const response = await fetch(url, config);

    if (response.status === 401) {
      throw new Error("Authentication failed");
    }

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}));
      throw new Error(
        errorData.error ||
          errorData.message ||
          `HTTP error! status: ${response.status}`
      );
    }

    const contentType = response.headers.get("content-type");
    if (contentType && contentType.includes("application/json")) {
      return (await response.json()) as T;
    }
    return response as unknown as T;
  }

  private jsonBody(body?: unknown) {
    return body !== undefined ? { body: JSON.stringify(body) } : {};
  }

  async get<T>(endpoint: string) {
    return this.request<T>(endpoint, { method: "GET" });
  }
  async post<T>(endpoint: string, body?: unknown) {
    return this.request<T>(endpoint, {
      method: "POST",
      ...this.jsonBody(body),
    });
  }
  async put<T>(endpoint: string, body?: unknown) {
    return this.request<T>(endpoint, { method: "PUT", ...this.jsonBody(body) });
  }
  async patch<T>(endpoint: string, body?: unknown) {
    return this.request<T>(endpoint, {
      method: "PATCH",
      ...this.jsonBody(body),
    });
  }
  async delete<T>(endpoint: string) {
    return this.request<T>(endpoint, { method: "DELETE" });
  }

  // Auth
  async RegisterUser(data: any) {
    const response = await this.post<any>("/v1/auth/register/", data);
    localStorage.setItem("authToken", response.access);
    localStorage.setItem("refreshToken", response.refresf);
    return response;
  }
  async LoginUser(data: any) {
    const response = await this.post<any>("/v1/auth/login/", data);
    localStorage.setItem("authToken", response.access);
    localStorage.setItem("refreshToken", response.refresf);
    return response;
  }

  // Products
  async getProducts() {
    return this.get("/v1/products/");
  }
  async createProduct(payload: any) {
    return this.post("/v1/products/", payload);
  }
  async updateProduct(id: string | number, payload: any) {
    return this.put(`/v1/products/${id}/`, payload);
  }
  async deleteProduct(id: string | number) {
    return this.delete(`/v1/products/${id}/`);
  }
}

export const apiClient = new ApiClient(API_BASE_URL);

import { jwtDecode } from "jwt-decode";

interface JWTPayload {
  user_id: string;
  email: string;
  role: string;
  exp: number;
  iat: number;
}

export const isTokenValid = (token: string): boolean => {
  try {
    const decoded = jwtDecode<JWTPayload>(token);
    const currentTime = Date.now() / 1000;
    return decoded.exp > currentTime;
  } catch {
    return false;
  }
};

export const getTokenPayload = (token: string): JWTPayload | null => {
  try {
    return jwtDecode<JWTPayload>(token);
  } catch {
    return null;
  }
};

export const getAuthHeaders = (): Record<string, string> => {
  const token = localStorage.getItem("authToken");
  return token ? { Authorization: `Bearer ${token}` } : {};
};

export const redirectBasedOnRole = (role: string): string => {
  switch (role) {
    case "Vendor":
      return "/Vendor/dashboard";
    case "admin":
      return "/admin/dashboard";
    default:
      return "/";
  }
};

export const formatUserRole = (role: string, language: string): string => {
  const roleMap = {
    vendor: language === "ar" ? "بائع":"Vendor",
    admin: language === "ar" ? "مدير" : "Admin",
    individual: language === "ar" ? "فرد" : "Individual",
  };

  return roleMap[role as keyof typeof roleMap] || role;
};

export const validateemail = (email: string): boolean => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
};

export const validatePassword = (
  password: string
): { isValid: boolean; errors: string[] } => {
  const errors: string[] = [];

  if (password.length < 8) {
    errors.push("Password must be at least 8 characters long");
  }

  if (!/[A-Z]/.test(password)) {
    errors.push("Password must contain at least one uppercase letter");
  }

  if (!/[a-z]/.test(password)) {
    errors.push("Password must contain at least one lowercase letter");
  }

  if (!/\d/.test(password)) {
    errors.push("Password must contain at least one number");
  }

  return {
    isValid: errors.length === 0,
    errors,
  };
};
