          {/* 🖥️ Desktop Navigation */}
          <div className="hidden lg:flex items-center gap-10">
            {navItems.map((item) =>
              item.key === "contact" ? (
                <Button
                  key={item.key}
                  onClick={() => handleNavigation(item.key)}
                  className="px-5 py-2 bg-[#DF1783] text-white rounded-full hover:bg-pink-500 transition-all"
                >
                  {item.label}
                </Button>
              ) : item.key === "services" ? (
                // 🌐 Services Dropdown with arrow indicator
                <div key={item.key} className="relative">
                  <div
                    onClick={() => setIsServicesOpen((prev) => !prev)}
                    className={`flex items-center gap-1 text-sm font-medium transition-all duration-300 cursor-pointer ${
                      location.pathname.includes("/services/")
                        ? "text-blue-600 dark:text-brand"
                        : "text-muted-foreground hover:text-primary dark:hover:text-primary hover:scale-105"
                    }`}
                  >
                    {item.label}
                    <motion.span
                      animate={{ rotate: isServicesOpen ? 180 : 0 }}
                      transition={{ duration: 0.3 }}
                    >
                      <ChevronDown className="h-4 w-4 mt-[1px]" />
                    </motion.span>
                  </div>

                  {isServicesOpen && (
                    <motion.div
                      initial={{ opacity: 0, y: -5 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: -5 }}
                      transition={{ duration: 0.2 }}
                      className="absolute left-0 mt-2 w-64 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg shadow-lg transition-all duration-300 z-50"
                      onMouseLeave={() => setIsServicesOpen(false)}
                    >
                      {serviceHubs(t).map((hub) => {
                        const isActive =
                          location.pathname === `/services/${hub.id}`;
                        return (
                          <div
                            key={hub.id}
                            onClick={() => {
                              navigate(`/services/${hub.id}`);
                              setIsServicesOpen(false);
                              window.scrollTo({ top: 0, behavior: "smooth" });
                            }}
                            className={`px-4 py-2 text-sm cursor-pointer transition-all rounded-sm 
        ${
          isActive
            ? "bg-gradient-to-r from-primary to-brand text-white font-semibold shadow-md"
            : "text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700"
        }`}
                          >
                            {hub.title}
                          </div>
                        );
                      })}
                    </motion.div>
                  )}
                </div>
              ) : (
                <div
                  key={item.key}
                  onClick={() => handleNavigation(item.key)}
                  className={`text-sm font-medium transition-all duration-300 cursor-pointer ${
                    (location.pathname === "/" && item.key === "home") ||
                    location.pathname === `/${item.key}`
                      ? "text-blue-600 dark:text-brand"
                      : "text-muted-foreground hover:text-primary dark:hover:text-primary hover:scale-105"
                  }`}
                >
                  {item.label}
                </div>
              )
            )}
          </div>

          {/* 🌗 Theme, Language & Auth Buttons */}
          <div className="flex items-center justify-end gap-2 sm:gap-3 shrink-0 rtl:flex-row-reverse">
            {/* Auth Button */}
            {/* {isAuthenticated ? (
              <CustomTooltip label={t("nav.settings")} side="bottom">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => navigate("/settings")}
                  className="bg-primary text-white border-primary hover:bg-primary/70 dark:text-white dark:border-primary dark:hover:bg-gray-800 rounded-full w-9 h-9 p-0 flex items-center justify-center"
                >
                  <Settings className="h-4 w-4" />
                </Button>
              </CustomTooltip>
            ) : (
              <CustomTooltip label={t("auth.login")} side="bottom">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => navigate("/login")}
                  className="text-white bg-primary border-primary hover:bg-primary/70 dark:text-white dark:border-primary dark:hover:bg-gray-800 rounded-full w-9 h-9 p-0 flex items-center justify-center"
                >
                  <img
                    src={login}
                    alt="Login"
                    className="h-5 w-5 rounded-full object-cover"
                  />
                </Button>
              </CustomTooltip>
            )} */}

            {/* Theme Toggle */}
            <CustomTooltip
              label={theme === "light" ? t("nav.lightMode") : t("nav.darkMode")}
              side="bottom"
            >
              <Button
                variant="outline"
                size="sm"
                onClick={toggleTheme}
                className="text-white bg-primary border-primary hover:bg-primary/70 dark:text-white dark:border-primary dark:hover:bg-gray-800 rounded-full w-9 h-9 p-0 flex items-center justify-center"
              >
                {theme === "light" ? (
                  <Moon className="h-4 w-4" />
                ) : (
                  <Sun className="h-4 w-4" />
                )}
              </Button>
            </CustomTooltip>

            {/* Language Toggle */}
            <CustomTooltip label={t("nav.language")} side="bottom">
              <Button
                variant="outline"
                size="sm"
                onClick={toggleLanguage}
                className="text-white bg-primary border-primary hover:bg-primary/70 dark:text-white dark:border-primary dark:hover:bg-gray-800 rounded-full w-9 h-9 p-0 flex items-center justify-center"
              >
                <Globe className="h-4 w-4" />
              </Button>
            </CustomTooltip>

            {/* Mobile Menu Button */}
            <CustomTooltip label={t("nav.menu")} side="bottom">
              <Button
                variant="outline"
                size="sm"
                className="lg:hidden text-white bg-primary border-primary hover:bg-primary/70 dark:text-white dark:border-primary dark:hover:bg-gray-800 rounded-full w-9 h-9 p-0 flex items-center justify-center"
                onClick={() => setIsMenuOpen(true)}
              >
                <Menu className="h-4 w-4" />
              </Button>
            </CustomTooltip>
          </div>
        </div>

        {/* 📱 Mobile Navigation */}
        {isMenuOpen && (
          <motion.div
            initial={{
              opacity: 0,
              x: i18n.language === "ar" ? 100 : -100,
            }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.3 }}
            className={`fixed top-0 ${
              i18n.language === "ar" ? "right-0" : "left-0"
            } w-64 max-w-[80%] h-full bg-gray-700 text-white border-primary dark:bg-gray-700 dark:text-white dark:border-primary shadow-lg z-50 p-4 overflow-y-auto`}
            dir={i18n.language === "ar" ? "rtl" : "ltr"}
          >
            <div className="flex justify-end mb-4">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setIsMenuOpen(false)}
                className="p-2 text-white bg-primary hover:text-primary transition-colors"
              >
                <X className="h-5 w-5" />
              </Button>
            </div>

            <div className="space-y-2">
              {navItems.map((item) =>
                item.key === "services" ? (
                  <div key={item.key} className="space-y-1">
                    <button className="block w-full text-left px-4 py-2 text-sm font-semibold text-brand">
                      {item.label}
                    </button>
                    {serviceHubs(t).map((hub) => (
                      <button
                        key={hub.id}
                        onClick={() => {
                          navigate(`/services/${hub.id}`);
                          setIsMenuOpen(false);
                          window.scrollTo({ top: 0, behavior: "smooth" });
                        }}
                        className="block w-full text-left px-6 py-1 text-sm text-gray-300 hover:text-primary"
                      >
                        • {hub.title}
                      </button>
                    ))}
                  </div>
                ) : item.key === "contact" ? (
                  <Button
                    key={item.key}
                    onClick={() => handleNavigation(item.key)}
                    className="w-full px-4 py-2 bg-[#DF1783] text-white rounded-full hover:bg-pink-500 transition-all"
                  >
                    {item.label}
                  </Button>
                ) : (
                  <button
                    key={item.key}
                    onClick={() => handleNavigation(item.key)}
                    className={`block w-full text-left px-4 py-2 text-sm font-medium transition-colors hover:text-primary ${
                      (location.pathname === "/" && item.key === "home") ||
                      location.pathname === `/${item.key}`
                        ? "text-blue-600 dark:text-brand"
                        : "text-muted-foreground hover:text-primary dark:hover:text-primary hover:scale-105"
                    }`}
                  >
                    {item.label}
                  </button>
                )
              )}
            </div>
          </motion.div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;

import { useEffect } from "react";
import { useLocation } from "react-router-dom";

const ScrollToTop = () => {
  const location = useLocation();

  useEffect(() => {
    window.scrollTo({ top: 0, left: 0, behavior: "smooth" });
  }, [location.pathname]);

  return null;
};

export default ScrollToTop;

