  // تعريف مصفوفة الـ partners هنا في المستوى الصحيح
  const parttners = [
    parttner1,
    parttner2,
    parttner3,
    parttner4,
    parttner5,
    parttner6,
    parttner7,
    parttner8,
    parttner9,
    parttner10,
    parttner11,
