                  <p className="text-sm text-gray-500 dark:text-gray-400">
                    {data.testimonial.rating}
                  </p>
                </div>
              </div>

              <div className="relative mt-2">
                <div className="absolute -top-4 -left-2 sm:-left-4 text-primary/20 dark:text-brand/20">
                  <svg
                    className="w-8 h-8"
                    fill="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path d="M4.583 17.321C3.553 16.227 3 15 3 13.011c0-3.5 2.457-6.637 6.03-8.188l.893 1.378c-3.335 1.804-3.987 4.145-4.247 5.621.537-.278 1.24-.375 1.929-.311 1.804.167 3.226 1.648 3.226 3.489a3.5 3.5 0 01-3.5 3.5c-1.073 0-2.099-.49-2.748-1.179zm10 0C13.553 16.227 13 15 13 13.011c0-3.5 2.457-6.637 6.03-8.188l.893 1.378c-3.335 1.804-3.987 4.145-4.247 5.621.537-.278 1.24-.375 1.929-.311 1.804.167 3.226 1.648 3.226 3.489a3.5 3.5 0 01-3.5 3.5c-1.073 0-2.099-.49-2.748-1.179z" />
                  </svg>
                </div>

                <blockquote className="text-base sm:text-lg md:text-xl text-gray-700 dark:text-gray-300 leading-relaxed font-medium pl-6 sm:pl-10 mt-4 sm:mt-0">
                  {data.testimonial.quote}
                </blockquote>
              </div>
            </div>
          </motion.div>
        </section>

        {/* Call to Action */}
        <section className="py-16 bg-gray-50 dark:bg-gray-700 rounded-3xl">
          <div className="max-w-4xl mx-auto px-6 text-center">
            <motion.div
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
                {t("cta.readyToStart", "Ready to Start Your Project?")}
              </h3>
              <p className="text-xl text-gray-600 dark:text-gray-300 mb-8">
                {t(
                  "cta.turnIdeasToReality",
                  "Let's turn your ideas into reality"
                )}
              </p>
              <Button
                size="lg"
                onClick={() => navigate("/contact")}
                className="bg-[#DF1783] text-white text-lg px-10 py-5 rounded-full hover:scale-105 hover:shadow-2xl hover:bg-pink-500 transition-all duration-300"
              >
                {t("cta.talkToExperts", "Talk to Our Experts")}
              </Button>
            </motion.div>
          </div>
        </section>
      </div>
    </section>
  );
}

import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import elazaby from "@/assets/images/elazaby.jpg";
import ghazala from "@/assets/images/ghazala.jpg";
import habib from "@/assets/images/habib.jpg";
const projects = [
  {
    id: 1,
    key: "elEzabyProject",
    image: elazaby,
  },
  {
    id: 2,
    key: "ghazalaProject",
    image: ghazala,
  },
  {
    id: 3,
    key: "habibProject",
    image: habib,
  }
];

export default function Projects() {
  const { t } = useTranslation();
  const navigate = useNavigate();

  const handleProjectClick = (key: string) => {
    navigate(`/projects/${key}`);
  };

  return (
    <section className="py-20 dark:bg-gray-800 min-h-screen">
      <div className="max-w-6xl mx-auto px-8">
        {/* Section Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
          className="text-center mb-14"
        >
          <h2 className="text-3xl md:text-4xl font-bold">
            {t("projects.title")}
          </h2>
          <p className="text-gray-500 dark:text-gray-400 mt-2">
            {t("projects.subtitle")}
          </p>
        </motion.div>

        {/* Projects Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {projects.map((project, index) => (
            <motion.div
              key={project.id}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              className="bg-white dark:bg-gray-700 rounded-2xl shadow-md cursor-pointer overflow-hidden"
              onClick={() => handleProjectClick(project.key)}
            >
              <img
                src={project.image}
                alt={t(`projects.${project.key}.title`)}
                className="w-full h-56 object-fill"
              />
              <div className="p-4">
                <p className="text-gray-500 dark:text-gray-400 text-sm mb-1">
                  {t(`projectDetails.${project.key}.tag`)}
                </p>
                <h3 className="text-lg font-semibold">
                  {t(`projectDetails.${project.key}.title`)} 
                </h3>
                <p className="text-gray-600 dark:text-gray-300 text-sm mt-2">
                  {t(`projectDetails.${project.key}.desc`)}
                </p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}

