import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { motion } from "framer-motion";
import { Menu, X, Sun, Moon, Globe, ChevronDown } from "lucide-react";
// import { Settings } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useTheme } from "@/contexts/ThemeContext";
// import { useAuth } from "@/contexts/AuthContext";
import { useLocation, useNavigate } from "react-router-dom";
import CustomTooltip from "../ui/CustomTooltip";
import { serviceHubs } from "@/data/serviceHubs";
import logo from "../../assets/images/stack-hubs-logo.png";
// import login from "../../assets/images/login.jpg";

const Navbar: React.FC = () => {
  const { t, i18n } = useTranslation();
  const { theme, toggleTheme } = useTheme();
  // const { isAuthenticated } = useAuth();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isServicesOpen, setIsServicesOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { key: "home", label: t("nav.home") },
    // { key: "about", label: t("nav.about") },
    { key: "services", label: t("nav.services") },
    { key: "loyal", label: t("nav.loyal") },
    { key: "partner", label: t("nav.partner") },
    { key: "projects", label: t("nav.projects") },
    { key: "store", label: t("nav.store") },
    { key: "contact", label: t("nav.contact") },
  ];

  const toggleLanguage = () => {
    const newLang = i18n.language === "en" ? "ar" : "en";
    i18n.changeLanguage(newLang);
  };

  const handleNavigation = (page: string) => {
    const pathMap: Record<string, string> = {
      home: "/",
      services: "/services",
      loyal: "/loyal",
      partner: "/partner",
      projects: "/projects",
      contact: "/contact",
      store: "http://store.stackhubs.com/", 
    };

    if (page === "store") {
      setIsMenuOpen(false);
      setIsServicesOpen(false);

      if (window.innerWidth < 768) {
        window.location.href = pathMap[page];
      } else {
        window.open(pathMap[page], "_blank");
      }
      return;
    }

    navigate(pathMap[page] || "/");
    window.scrollTo({ top: 0, behavior: "smooth" });
    setIsMenuOpen(false);
    setIsServicesOpen(false);
  };

  return (
    <nav className="bg-white shadow-lg sticky top-0 w-full z-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-2">
        <div className="flex items-center h-16 justify-between">
          {/* üß≠ Logo */}
          <motion.div
            className="flex items-center gap-2 cursor-pointer min-w-0"
            onClick={() => handleNavigation("home")}
          >
            <img
              src={logo}
              alt="StackHubs Logo"
              className="h-8 w-auto sm:h-10 max-w-[120px] sm:max-w-[160px]"
            />
          </motion.div>

          {/* üñ•Ô∏è Desktop Navigation */}
          <div className="hidden lg:flex items-center gap-10">
            {navItems.map((item) =>
              item.key === "contact" || item.key === "store" ? (
                <Button
                  key={item.key}
                  onClick={() => handleNavigation(item.key)}
                  className="px-5 py-2 bg-[#DF1783] text-white rounded-full hover:bg-pink-500 transition-all"
                >
                  {item.label}
                </Button>
              ) : item.key === "services" ? (
                <div key={item.key} className="relative">
                  <div
                    onClick={() => setIsServicesOpen((prev) => !prev)}
                    className={`flex items-center gap-1 text-sm font-medium transition-all duration-300 cursor-pointer ${
                      location.pathname.includes("/services/")
                        ? "text-blue-600 dark:text-brand"
                        : "text-muted-foreground hover:text-primary dark:hover:text-primary hover:scale-105"
                    }`}
                  >
                    {item.label}
                    <motion.span
                      animate={{ rotate: isServicesOpen ? 180 : 0 }}
                      transition={{ duration: 0.3 }}
                    >
                      <ChevronDown className="h-4 w-4 mt-[1px]" />
                    </motion.span>
                  </div>

                  {isServicesOpen && (
                    <motion.div
                      initial={{ opacity: 0, y: -5 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: -5 }}
                      transition={{ duration: 0.2 }}
                      className="absolute left-0 mt-2 w-64 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg shadow-lg transition-all duration-300 z-50"
                      onMouseLeave={() => setIsServicesOpen(false)}
                    >
                      {serviceHubs(t).map((hub) => {
                        const isActive =
                          location.pathname === `/services/${hub.id}`;
                        return (
                          <div
                            key={hub.id}
                            onClick={() => {
                              navigate(`/services/${hub.id}`);
                              setIsServicesOpen(false);
                              window.scrollTo({ top: 0, behavior: "smooth" });
                            }}
                            className={`px-4 py-2 text-sm cursor-pointer transition-all rounded-sm 
        ${
          isActive
            ? "bg-gradient-to-r from-primary to-brand text-white font-semibold shadow-md"
            : "text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700"
        }`}
                          >
