export type Product = {
  id: number;
  name: string;
  price: number;
  image: string;
};

const products: Product[] = [
  {
    id: 1,
    name: "Rauter",
    price: 100,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 2,
    name: "Laptop",
    price: 150,
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYhhr9_D_5A4uz3UrkZQSI3hwzLzJ1v_kOuw&s",
  },
  {
    id: 3,
    name: "Smartphone",
    price: 250,
    image:
      "data:image/jpeg;base64,",
  },
];
export function fetchProducts(): Promise<Product[]> {
  return new Promise((resolve) => {
    setTimeout(() => resolve(products), 700);
  });
}

