              icon: facebook,
            },
            {
              name: "Instagram",
              href: "https://www.instagram.com/stackhubs_global",
              icon: instagram,
            },
            {
              name: "Twitter",
              href: "https://x.com/Stackhubs_Globa",
              icon: twitter,
            },
            {
              name: "LinkedIn",
              href: "https://www.linkedin.com/company/stackhubs-global/",
              icon: linkedin,
            },
          ].map((social, index) => (
            <motion.a
